var scClient = require('socketcluster-client');
var options = {
  protocol: 'http',
  hostname: 'acc.ledwifi.de',
  port: 8051,
  autoReconnect: true
};

var socket = scClient.connect(options);


var lightChannel 	  = socket.subscribe('lightcontrol');

lightChannel.watch(function (data) {

    console.log('lightcontrol['+data[0].node_id+'] '+data[0].timestamp+'::'+data[0].content+'>'+JSON.stringify(data[1],null,2));
});


